    console.log($(rowProduct).length);

    if ($(rowProduct).length === 1) {
      var unitsIdentifier = $("#units-".concat(productName));
      var totalCostIdentifier = $("#total-cost-of-".concat(productName));
      var oldUnits = unitsIdentifier.html();
      var oldItemCostDollarAmount = totalCostIdentifier.html();
      var oldItemCostNumber = oldItemCostDollarAmount.substr(oldItemCostDollarAmount.indexOf('$') + 1);
      var newUnits = Number(productUnits) + Number(oldUnits);
      var newTotalItemCost = Number(productValue) + Number(oldItemCostNumber);
      unitsIdentifier.html(newUnits); // eslint-disable-next-line no-useless-concat

      totalCostIdentifier.html("$".concat(newTotalItemCost));
    } else {
      $('#cart-list > tbody').append("<tr class=\"product-order\">\n                    <td class='productID' hidden>".concat(productID, "</td>\n                    <td id='").concat(productName, "' class=\"productName\">").concat(productName, "</td>\n                    <td id='units-").concat(productName, "' class=\"productUnits\">").concat(productUnits, "</td>\n                    // eslint-disable-next-line max-len\n                    <td id='total-cost-of-").concat(productName, "' class=\"productCost\">$").concat(productValue, "</td>\n                  </tr>"));
    }
