  $('#new-password').val('');
  $('#new-password-confirm').val('');
}

function registerUser() {
  console.log('Attempting to Register a New User...');
  var user = getNewAccountInfo();
  var userJSON = {
    username: user.username,
    password: user.password
  };
  $.ajax({
    type: 'POST',
    // GET, POST, PUT
