  font-weight: 500;
}

.cost-fields .checkout-total .total-label {
  width: auto;
}

span.total-value.cost-value {
  margin: auto;
  width: 70%;
  padding-left: 9%;
}

.card-verification input[type="password"] {
  width: 20%;
}

.field-is-valid {
  border: 2px solid lightgreen !important;
  -webkit-box-shadow: 0 0 8px 2px rgba(152, 251, 152, 0.75);
  box-shadow: 0 0 8px 2px rgba(152, 251, 152, 0.75);
  background: url(" + ___CSS_LOADER_URL___0___ + ");
}

.field-is-invalid {
  border: 2px solid salmon !important;
  -webkit-box-shadow: 0 0 8px 2px rgba(250, 128, 114, 0.75);
  box-shadow: 0 0 8px 2px rgba(250, 128, 114, 0.75);
  background: url(" + ___CSS_LOADER_URL___1___ + ");
}

