#register-user-btn.disabled-button,
#submit-payment-information-btn.disabled-button,
#shipping-info-submit-btn.disabled-button,
#submit-order-information-btn.disabled-button {
  /* border: 1px solid #ababab; */
  background-color: rgba(54, 51, 58, 0.61);
  color: #a9a6a6;
  -webkit-box-shadow: 0 0 8px 2px #595959;
  box-shadow: 0 0 8px 2px #595959;
}

#register-user-btn.disabled-button:hover,
#submit-payment-information-btn.disabled-button:hover,
#shipping-info-submit-btn.disabled-button:hover,
#submit-order-information-btn.disabled-button:hover {
  -webkit-box-shadow: 0 0 8px 6px rgba(76, 6, 6, 0.75);
  box-shadow: 0 0 8px 6px rgba(76, 6, 6, 0.75);
}
